	public static Stream<BrandItem> loadInitialList() throws IOException {
		ArrayList<BrandItem> brandList = new ArrayList<>();
		String inputfile = "data" + File.separator + "Phase1Merging.txt";
		System.out.println(inputfile);
		for (String url : Datasets.readRawUrlsFromFile(inputfile)) {
			BrandItem item = new BrandItem();
			item.setUrl(url);
			item.setBrand(PageUrlUtil.findFullSubDomain(url));
			brandList.add(item);
			
			URL domainUrl = new URL(url);
			 Scanner sc = new Scanner(domainUrl.openStream());
			 StringBuffer sb = new StringBuffer();
			 while(sc.hasNext()) {
		         sb.append(sc.next());
		         //System.out.println(sc.next());
		      }
			  result = sb.toString();
		      System.out.println(result);
		      continue;
		}
		return brandList.stream();
	}

